              fc.string().map(s => s + '@'), // Incomplete email
            ),
            bio: fc.string({ minLength: 501, maxLength: 1000 }) // Too long
          }),
          async (invalidData) => {
            const mockProfile = { ...generateValidUser(), id: mockCurrentUser.id };
