#!/bin/bash

# Read the tool input from stdin
INPUT=$(cat)
TOOL_NAME=$(echo "$INPUT" | jq -r '.tool_name')
FILE_PATH=$(echo "$INPUT" | jq -r '.tool_input.file_path // .tool_input.path // ""')

if [ -z "$FILE_PATH" ]; then
  exit 0
fi

# Check if this is a React component file (but not a test file)
if [[ "$FILE_PATH" == *.jsx ]] || [[ "$FILE_PATH" == *.tsx ]]; then
    # Skip if this is already a test file
    if [[ "$FILE_PATH" == *.test.* ]] || [[ "$FILE_PATH" == *.spec.* ]]; then
        exit 0
    fi

    echo "âš›ï¸ React Component Test Generator - Processing component..."
    echo "ğŸ“„ Component: $FILE_PATH"

    # Extract component info
    COMPONENT_DIR=$(dirname "$FILE_PATH")
    COMPONENT_BASENAME=$(basename "$FILE_PATH")
    COMPONENT_NAME=$(basename "${FILE_PATH%.*}")
    COMPONENT_EXT="${FILE_PATH##*.}"

    # Determine test file path
    if [[ "$COMPONENT_EXT" == "tsx" ]]; then
        TEST_FILE="${COMPONENT_DIR}/${COMPONENT_NAME}.test.tsx"
    else
        TEST_FILE="${COMPONENT_DIR}/${COMPONENT_NAME}.test.jsx"
    fi

    # Check if test file already exists
    if [ -f "$TEST_FILE" ]; then
        echo "â„¹ï¸ Test file already exists: $TEST_FILE"
        echo "ğŸ’¡ Consider updating tests to match component changes"
    else
        echo "ğŸ§ª Generating test file: $TEST_FILE"

        # Create test file content
        cat > "$TEST_FILE" << EOF
import { render, screen } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import $COMPONENT_NAME from './$COMPONENT_BASENAME';

describe('$COMPONENT_NAME', () => {
  it('renders without crashing', () => {
    render(<$COMPONENT_NAME />);
  });

  it('displays expected content', () => {
    render(<$COMPONENT_NAME />);
    // Add assertions here
    // expect(screen.getByText('expected text')).toBeInTheDocument();
  });

  // Add more component-specific tests here
  // Example: testing props, user interactions, etc.
  // 
  // it('handles user interactions', async () => {
  //   const user = userEvent.setup();
  //   render(<$COMPONENT_NAME />);
  //   // await user.click(screen.getByRole('button'));
  //   // expect(...);
  // });
});
EOF

        if [ $? -eq 0 ]; then
            echo "âœ… Test file created successfully!"
            echo "ğŸ“ Test file: $TEST_FILE"
        else
            echo "âŒ Failed to create test file"
        fi
    fi

    # Additional suggestions based on component analysis
    echo ""
    echo "ğŸ” Component Analysis:"

    if [ -f "$FILE_PATH" ]; then
        # Check for props interface/type
        if grep -q "interface.*Props\|type.*Props" "$FILE_PATH"; then
            echo "  â€¢ ğŸ’¡ Props interface detected - consider testing different prop combinations"
        fi

        # Check for hooks usage
        if grep -q "useState\|useEffect\|useContext" "$FILE_PATH"; then
            echo "  â€¢ ğŸ’¡ React hooks detected - consider testing state changes and side effects"
        fi

        # Check for event handlers
        if grep -q "onClick\|onChange\|onSubmit" "$FILE_PATH"; then
            echo "  â€¢ ğŸ’¡ Event handlers detected - consider testing user interactions"
        fi

        # Check for conditional rendering
        if grep -q "&&\|?.*:" "$FILE_PATH"; then
            echo "  â€¢ ğŸ’¡ Conditional rendering detected - test different rendering scenarios"
        fi
    fi

    echo ""
    echo "ğŸ’¡ Testing Best Practices:"
    echo "  â€¢ Test component behavior, not implementation details"
    echo "  â€¢ Use accessible queries (getByRole, getByLabelText)"
    echo "  â€¢ Test user interactions with userEvent"
    echo "  â€¢ Mock external dependencies and API calls"
    echo "  â€¢ Test edge cases and error states"

    echo ""
    echo "ğŸ¯ Test generation complete!"

else
    echo "â„¹ï¸ File is not a React component: $FILE_PATH"
fi

exit 0
