```typescript
// src/events/EventBus.ts
export interface Event {
    type: string;
    payload: any;
    timestamp: Date;
    correlationId?: string;
}

export class EventBus {
    private connection: Connection;
    private channel: Channel;

    constructor(private rabbitmqUrl: string) {}

    async connect(): Promise<void> {
        this.connection = await amqp.connect(this.rabbitmqUrl);
        this.channel = await this.connection.createChannel();

        // Setup dead letter queue
        await this.channel.assertExchange('dlx', 'direct', { durable: true });
        await this.channel.assertQueue('dead-letters', {
            durable: true,
            arguments: {
                'x-message-ttl': 86400000 // 24 hours
            }
        });
        await this.channel.bindQueue('dead-letters', 'dlx', 'dead-letter');
    }

    async publish(exchange: string, routingKey: string, event: Event): Promise<void> {
        const eventWithId = {
            ...event,
            id: uuidv4(),
            timestamp: new Date()
        };

        await this.channel.publish(
            exchange,
            routingKey,
            Buffer.from(JSON.stringify(eventWithId)),
            {
                persistent: true,
                correlationId: event.correlationId,
                timestamp: Date.now()
            }
        );
    }

    async subscribe(
        queue: string,
        handler: (event: Event) => Promise<void>,
        options: {
            exchange?: string;
            routingKey?: string;
            maxRetries?: number;
        } = {}
    ): Promise<void> {
        const { exchange = '', routingKey = '', maxRetries = 3 } = options;

        // Setup queue with dead letter exchange
        await this.channel.assertQueue(queue, {
            durable: true,
            arguments: {
                'x-dead-letter-exchange': 'dlx',
                'x-dead-letter-routing-key': 'dead-letter'
            }
        });

        if (exchange) {
            await this.channel.assertExchange(exchange, 'topic', { durable: true });
            await this.channel.bindQueue(queue, exchange, routingKey);
        }

        await this.channel.consume(queue, async (msg) => {
            if (!msg) return;

            try {
                const event = JSON.parse(msg.content.toString());
                await handler(event);
                this.channel.ack(msg);
            } catch (error) {
                console.error('Event processing error:', error);

                const retryCount = (msg.properties.headers?.['x-retry-count'] as number) || 0;

                if (retryCount < maxRetries) {
                    // Retry with exponential backoff
                    const delay = Math.pow(2, retryCount) * 1000;

                    setTimeout(() => {
                        this.channel.publish(
                            '',
                            queue,
                            msg.content,
                            {
                                ...msg.properties,
                                headers: {
                                    ...msg.properties.headers,
                                    'x-retry-count': retryCount + 1
                                }
                            }
                        );
                    }, delay);
                }

                this.channel.nack(msg, false, false); // Send to DLQ
            }
        });
    }
}

