        const cached = await redis.get(cacheKey);
        if (cached) {
            return JSON.parse(cached);
        }

        const result = await this.optimizedQuery(sql, params);
        await redis.setex(cacheKey, ttl, JSON.stringify(result.rows));

        return result.rows;
    }
}

