        ...renderOptions
    } = options;

    function Wrapper({ children }) {
        return (
            <Provider store={store}>
                <MemoryRouter>
                    <ThemeProvider theme={defaultTheme}>
                        {children}
                    </ThemeProvider>
                </MemoryRouter>
            </Provider>
        );
    }

    return {
        store,
        ...render(ui, { wrapper: Wrapper, ...renderOptions })
    };
};
```

