        max_value: 7
      }
    ]
  }
];

const registerCommands = await discord.registerSlashCommands(commands);

// Handle slash command interactions
discord.on('interactionCreate', async (interaction) => {
  if (!interaction.isChatInputCommand()) return;

  switch (interaction.commandName) {
    case 'ping':
      await interaction.reply({
        content: `Pong! Latency: ${discord.ws.ping}ms`,
        ephemeral: true
      });
      break;

    case 'userinfo':
      const user = interaction.options.getUser('user');
      const member = await interaction.guild.members.fetch(user.id);

      const userEmbed = {
        title: `User Info: ${user.tag}`,
        thumbnail: { url: user.displayAvatarURL() },
        fields: [
          { name: 'ID', value: user.id, inline: true },
          { name: 'Created', value: `<t:${Math.floor(user.createdTimestamp / 1000)}:F>`, inline: true },
          { name: 'Joined', value: `<t:${Math.floor(member.joinedTimestamp / 1000)}:F>`, inline: true },
          { name: 'Roles', value: member.roles.cache.map(r => r.toString()).join(' '), inline: false }
        ],
        color: member.displayHexColor
      };

      await interaction.reply({ embeds: [userEmbed] });
      break;

    case 'ban':
      const targetUser = interaction.options.getUser('user');
      const reason = interaction.options.getString('reason') || 'No reason provided';
      const deleteDays = interaction.options.getInteger('delete_days') || 0;

      try {
        await interaction.guild.members.ban(targetUser, {
          reason: `${reason} | Banned by ${interaction.user.tag}`,
          deleteMessageDays: deleteDays
        });

        await interaction.reply({
          content: `Successfully banned ${targetUser.tag} for: ${reason}`,
          ephemeral: true
        });
      } catch (error) {
        await interaction.reply({
          content: `Failed to ban user: ${error.message}`,
          ephemeral: true
        });
      }
      break;
  }
});
```

### Advanced Moderation System
```javascript
// Auto-moderation configuration
const autoMod = {
  spamDetection: {
    enabled: true,
    maxMessages: 5,
    timeWindow: 10000, // 10 seconds
    punishment: 'timeout', // timeout, kick, ban
    duration: 300000 // 5 minutes timeout
  },
  wordFilter: {
    enabled: true,
    blockedWords: ['spam', 'scam', 'inappropriate'],
    action: 'delete',
    warnUser: true
  },
  linkFilter: {
    enabled: true,
    allowedDomains: ['youtube.com', 'github.com'],
    blockInvites: true
  }
};

// Message monitoring
discord.on('messageCreate', async (message) => {
  if (message.author.bot) return;

  // Spam detection
  if (autoMod.spamDetection.enabled) {
    const userMessages = recentMessages.get(message.author.id) || [];
    userMessages.push(Date.now());

    // Remove old messages outside time window
    const validMessages = userMessages.filter(
      timestamp => Date.now() - timestamp < autoMod.spamDetection.timeWindow
    );

    if (validMessages.length > autoMod.spamDetection.maxMessages) {
      await handleSpam(message, autoMod.spamDetection.punishment);
      return;
    }

    recentMessages.set(message.author.id, validMessages);
  }

  // Word filter
  if (autoMod.wordFilter.enabled) {
    const hasBlockedWord = autoMod.wordFilter.blockedWords.some(
      word => message.content.toLowerCase().includes(word.toLowerCase())
    );

    if (hasBlockedWord) {
      await message.delete();
      if (autoMod.wordFilter.warnUser) {
        await message.author.send({
          embeds: [{
            title: 'Message Removed',
            description: 'Your message was removed for containing inappropriate content.',
            color: 0xff0000
          }]
        });
      }
      return;
    }
  }

  // Link filter
  if (autoMod.linkFilter.enabled) {
    const urlRegex = /(https?:\/\/[^\s]+)/g;
    const urls = message.content.match(urlRegex) || [];

    for (const url of urls) {
      const domain = new URL(url).hostname;

      if (autoMod.linkFilter.blockInvites && url.includes('discord.gg')) {
        await message.delete();
        await warnUser(message.author, 'Posted unauthorized invite link');
        return;
      }

      if (!autoMod.linkFilter.allowedDomains.includes(domain)) {
        await message.delete();
        await warnUser(message.author, 'Posted unauthorized link');
        return;
      }
    }
  }
});

// Warning system
const warnUser = async (user, reason) => {
  const warnings = await database.getUserWarnings(user.id);

  await database.addWarning({
    userId: user.id,
    reason: reason,
    timestamp: Date.now(),
    moderator: 'AutoMod'
  });

  const warningCount = warnings.length + 1;

  // Escalation system
  if (warningCount >= 5) {
    await user.ban({ reason: 'Exceeded warning limit (5 warnings)' });
  } else if (warningCount >= 3) {
    await user.timeout(86400000, 'Multiple warnings (24 hour timeout)'); // 24 hours
  } else if (warningCount >= 2) {
    await user.timeout(3600000, 'Multiple warnings (1 hour timeout)'); // 1 hour
  }

  // Notify user
  try {
    await user.send({
      embeds: [{
        title: 'Warning Received',
        description: `You have been warned for: ${reason}`,
        fields: [
          { name: 'Warning Count', value: `${warningCount}/5`, inline: true },
          { name: 'Next Punishment', value: getNextPunishment(warningCount), inline: true }
        ],
        color: 0xffa500
      }]
    });
  } catch (error) {
    console.log('Could not DM user about warning');
  }
};
```

