      'UPDATE products SET ? WHERE id = ?',
      [updates, productId]
    );

    // Invalidate related caches
    await this.cache.del(`product:stats:${productId}`);
    await this.cache.del(`product:${productId}`);
    this.memoryCache.delete(`product:stats:${productId}`);
  }
