    }

    if (!formData.email?.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Invalid email format';
    }

    if (formData.bio && formData.bio.length > 500) {
      newErrors.bio = 'Bio must be less than 500 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = async () => {
    if (!validateForm()) return;

    try {
      setLoading(true);
      const updatedProfile = await updateUserProfile(userId, formData);
      setProfile(updatedProfile);
      setEditing(false);
      toast.success('Profile updated successfully');
      onProfileUpdate?.(updatedProfile);
    } catch (error) {
      if (error.status === 409) {
        setErrors({ email: 'Email already exists' });
      } else {
        toast.error('Failed to update profile');
      }
    } finally {
      setLoading(false);
    }
  };

  const canEdit = editable && (currentUser?.id === userId || currentUser?.role === 'admin');

  if (loading && !profile) {
    return <div data-testid="loading-spinner">Loading...</div>;
  }

  if (!profile) {
    return <div data-testid="error-message">Profile not found</div>;
  }

  return (
    <div data-testid="user-profile-card" className="profile-card">
      <div className="profile-header">
        <img 
          src={profile.avatar || '/default-avatar.png'} 
          alt={`${profile.name}'s avatar`}
          data-testid="profile-avatar"
        />
        {editing ? (
          <input
            type="text"
            value={formData.name}
            onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
            data-testid="name-input"
            className={errors.name ? 'error' : ''}
            placeholder="Enter name"
          />
        ) : (
          <h2 data-testid="profile-name">{profile.name}</h2>
        )}
        {errors.name && <span data-testid="name-error" className="error">{errors.name}</span>}
      </div>

      <div className="profile-details">
        <div className="detail-item">
          <label>Email:</label>
          {editing ? (
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
              data-testid="email-input"
              className={errors.email ? 'error' : ''}
            />
          ) : (
            <span data-testid="profile-email">{profile.email}</span>
          )}
          {errors.email && <span data-testid="email-error" className="error">{errors.email}</span>}
        </div>

        <div className="detail-item">
          <label>Bio:</label>
          {editing ? (
            <textarea
              value={formData.bio}
              onChange={(e) => setFormData(prev => ({ ...prev, bio: e.target.value }))}
              data-testid="bio-input"
              className={errors.bio ? 'error' : ''}
              placeholder="Tell us about yourself..."
              maxLength={500}
            />
          ) : (
            <p data-testid="profile-bio">{profile.bio || 'No bio available'}</p>
          )}
          {errors.bio && <span data-testid="bio-error" className="error">{errors.bio}</span>}
        </div>
      </div>

      {canEdit && (
        <div className="profile-actions">
          {editing ? (
            <>
              <button
                onClick={handleSave}
                disabled={loading}
                data-testid="save-button"
                className="btn-primary"
              >
                {loading ? 'Saving...' : 'Save Changes'}
              </button>
              <button
                onClick={() => {
                  setEditing(false);
                  setFormData({
                    name: profile.name,
                    email: profile.email,
                    bio: profile.bio || ''
                  });
                  setErrors({});
                }}
                disabled={loading}
                data-testid="cancel-button"
                className="btn-secondary"
              >
                Cancel
              </button>
            </>
          ) : (
            <button
              onClick={() => setEditing(true)}
              data-testid="edit-button"
              className="btn-primary"
            >
              Edit Profile
            </button>
          )}
        </div>
      )}
    </div>
  );
};

export default UserProfileCard;
