### 4. **CLI Interface**

```bash
#!/bin/bash
# migration-cli.sh

command="$1"
shift

case "$command" in
    "create")
        name="$1"
        if [ -z "$name" ]; then
            echo "Usage: ./migration-cli.sh create <migration_name>"
            exit 1
        fi

        timestamp=$(date +%Y%m%d%H%M%S)
        filename="${timestamp}_${name}.js"

        cat > "migrations/$filename" << EOF
module.exports = {
    id: '${timestamp}_${name}',
    description: '${name}',

    up: async (db) => {
        // TODO: Implement migration
    },

    down: async (db) => {
        // TODO: Implement rollback
    },

    validate: async (db) => {
        // TODO: Implement validation
    }
};
EOF

        echo "‚úÖ Created migration: $filename"
        ;;

    "status")
        node -e "
            const { MigrationRunner } = require('./migration-runner');
            const db = require('./db');

            (async () => {
                const runner = new MigrationRunner(db);
                await runner.initialize();

                const pending = await runner.getPendingMigrations();
                const executed = await runner.getExecutedMigrations();

                console.log('Migration Status:');
                console.log('=================');
                console.log('Executed:', executed.length);
                console.log('Pending:', pending.length);

                if (pending.length > 0) {
                    console.log('\nPending migrations:');
                    pending.forEach(m => console.log('  -', m.id));
                }

                process.exit(0);
            })();
        "
        ;;

    "up")
        target="$1"
        node -e "
            const { MigrationRunner } = require('./migration-runner');
            const db = require('./db');

            (async () => {
                const runner = new MigrationRunner(db);
                await runner.initialize();
                await runner.migrateUp('$target');
                process.exit(0);
            })();
        "
        ;;

    "down")
        target="$1"
        echo "‚ö†Ô∏è  Are you sure you want to rollback? This may result in data loss."
        read -p "Type 'yes' to continue: " confirm

        if [ "$confirm" = "yes" ]; then
            node -e "
                const { MigrationRunner } = require('./migration-runner');
                const db = require('./db');

                (async () => {
                    const runner = new MigrationRunner(db);
                    await runner.initialize();
                    await runner.migrateDown('$target');
                    process.exit(0);
                })();
            "
        else
            echo "Rollback cancelled"
        fi
        ;;

    "validate")
        node -e "
            const { MigrationRunner } = require('./migration-runner');
            const db = require('./db');

            (async () => {
                const runner = new MigrationRunner(db);
                const issues = await runner.validateMigrations();

                if (issues.length === 0) {
                    console.log('‚úÖ All migrations are valid');
                } else {
                    console.log('‚ùå Migration validation issues:');
                    issues.forEach(issue => {
                        console.log('  -', issue.type + ':', issue.message, '(' + issue.migration + ')');
                    });
                    process.exit(1);
                }

                process.exit(0);
            })();
        "
        ;;

    *)
        echo "Usage: $0 {create|status|up|down|validate} [options]"
        echo ""
        echo "Commands:"
        echo "  create <name>     Create a new migration"
        echo "  status           Show migration status"
        echo "  up [target]      Run pending migrations"
        echo "  down [target]    Rollback migrations"
        echo "  validate         Validate migration integrity"
        exit 1
        ;;
esac
