  'sql-injection': (code) => {
    return code.replace(
      /database\.query\(`(.+?)\$\{(.+?)\}`\)/g,
      "database.query('$1?', [$2])"
    );
  },

  // Fix XSS vulnerabilities
  'xss-vulnerability': (code) => {
    return code.replace(
      /\.innerHTML\s*=\s*(.+)/g,
      '.textContent = $1'
    );
