### Pub/Sub Messaging
```javascript
// Publisher
const publishMessage = async (channel, message) => {
  const result = await redis.publish(channel, JSON.stringify({
    type: 'user_action',
    userId: 1000,
    action: 'purchase',
    productId: 'prod-123',
    timestamp: Date.now(),
    metadata: {
      amount: 99.99,
      currency: 'USD'
    }
  }));

  console.log(`Message sent to ${result} subscribers`);
};

// Subscriber
const subscriber = redis.duplicate();

subscriber.on('message', (channel, message) => {
  const data = JSON.parse(message);
  console.log(`Received on ${channel}:`, data);

  // Process the message based on type
  switch (data.type) {
    case 'user_action':
      handleUserAction(data);
      break;
    case 'system_alert':
      handleSystemAlert(data);
      break;
  }
});

subscriber.on('subscribe', (channel, count) => {
  console.log(`Subscribed to ${channel}. Total subscriptions: ${count}`);
});

// Subscribe to channels
const subscribe = await subscriber.subscribe('user:events', 'system:alerts', 'notifications');

// Pattern subscription
const patternSubscribe = await subscriber.psubscribe('user:*:events', 'system:*');
```

### Lua Scripting (Atomic Operations)
```javascript
// Rate limiting script
const rateLimitScript = `
  local key = KEYS[1]
  local window = tonumber(ARGV[1])
  local limit = tonumber(ARGV[2])
  local current_time = tonumber(ARGV[3])

  local current = redis.call('GET', key)
  if current == false then
    redis.call('SETEX', key, window, 1)
    return {1, limit - 1}
  end

  current = tonumber(current)
  if current < limit then
    local new_val = redis.call('INCR', key)
    local ttl = redis.call('TTL', key)
    return {new_val, limit - new_val}
  else
    local ttl = redis.call('TTL', key)
    return {current, 0, ttl}
  end
`;

// Execute rate limit check
const checkRateLimit = async (userId, windowSeconds = 60, maxRequests = 100) => {
  const result = await redis.eval(
    rateLimitScript,
    1,
    `rate_limit:${userId}`,
    windowSeconds,
    maxRequests,
    Math.floor(Date.now() / 1000)
  );

  return {
    currentRequests: result[0],
    remainingRequests: result[1],
    resetTime: result[2] || windowSeconds
  };
};

// Distributed lock script
const acquireLockScript = `
  local key = KEYS[1]
  local value = ARGV[1]
  local ttl = tonumber(ARGV[2])

  local current = redis.call('GET', key)
  if current == false then
    redis.call('SETEX', key, ttl, value)
    return 1
  elseif current == value then
    redis.call('EXPIRE', key, ttl)
    return 1
  else
    return 0
  end
`;

const acquireLock = async (lockName, identifier, ttlSeconds = 30) => {
  const result = await redis.eval(
    acquireLockScript,
    1,
    `lock:${lockName}`,
    identifier,
    ttlSeconds
  );

  return result === 1;
};
```

### Redis Streams (Advanced Messaging)
```javascript
// Add events to stream
const addToStream = await redis.xadd(
  'events:user_actions',
  '*', // Auto-generate ID
  'user_id', '1000',
  'action', 'purchase',
  'product_id', 'prod-123',
  'amount', '99.99',
  'timestamp', Date.now()
);

// Read from stream
const readStream = await redis.xread(
  'COUNT', 10,
  'STREAMS', 'events:user_actions', '0-0' // From beginning
);

// Consumer groups
const createGroup = await redis.xgroup(
  'CREATE', 'events:user_actions', 'analytics_group', '0', 'MKSTREAM'
);

// Read as consumer
const readAsConsumer = await redis.xreadgroup(
  'GROUP', 'analytics_group', 'consumer1',
  'COUNT', 5,
  'STREAMS', 'events:user_actions', '>'
);

// Acknowledge processed messages
const ackMessages = await redis.xack(
  'events:user_actions',
  'analytics_group',
  '1642784400000-0', '1642784401000-0'
);
